import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import MessageCard from './components/MessageCard';
import Footer from './components/Footer';
import { messageService } from './services/api';
import { useMessageCounter } from './hooks/useMessageCounter';
import './App.css';

function App() {
  const [currentMessage, setCurrentMessage] = useState(null);
  const [loading, setLoading] = useState(false);
  const [apiStatus, setApiStatus] = useState(null);
  const { count, incrementCounter } = useMessageCounter();

  // 페이지 로드 시 초기 메시지 가져오기
  useEffect(() => {
    const fetchInitial = async () => {
      setLoading(true);
      try {
        const message = await messageService.getRandomMessage();
        setCurrentMessage(message);
        incrementCounter();
      } catch (error) {
        console.error('초기 메시지 로딩 실패:', error);
      }
      setLoading(false);
    };

    const checkHealth = async () => {
      try {
        const health = await messageService.checkHealth();
        const stats = await messageService.getStats();
        setApiStatus({
          status: health.status === 'healthy' ? 'healthy' : 'unhealthy',
          message_count: stats.total_messages
        });
      } catch (error) {
        setApiStatus({
          status: 'unhealthy',
          message_count: 0
        });
      }
    };

    fetchInitial();
    checkHealth();
  }, []); // dependency array를 빈 배열로 변경

  const fetchNewMessage = async () => {
    setLoading(true);
    try {
      const message = await messageService.getRandomMessage();
      setCurrentMessage(message);
      incrementCounter();
    } catch (error) {
      console.error('새 메시지 로딩 실패:', error);
    }
    setLoading(false);
  };

  const handleShare = () => {
    // 공유 완료 후 추가 로직이 필요한 경우
  };

  const handleSpeak = () => {
    // 음성 재생 완료 후 추가 로직이 필요한 경우
  };

  const handleFavorite = (message) => {
    // 즐겨찾기 기능 구현
    const favorites = JSON.parse(localStorage.getItem('favorites') || '[]');
    const isAlreadyFavorite = favorites.some(fav => fav.id === message.id);
    
    if (!isAlreadyFavorite) {
      favorites.push(message);
      localStorage.setItem('favorites', JSON.stringify(favorites));
      alert('즐겨찾기에 추가되었습니다!');
    } else {
      alert('이미 즐겨찾기에 추가된 메시지입니다.');
    }
  };

  return (
    <div className="min-h-screen gradient-bg">
      <div className="container mx-auto px-4 py-8 max-w-4xl">
        <Header messageCount={count} />
        
        <main className="mb-12">
          <MessageCard
            message={currentMessage}
            onNewMessage={fetchNewMessage}
            loading={loading}
            onShare={handleShare}
            onSpeak={handleSpeak}
            onFavorite={handleFavorite}
          />
        </main>

        <Footer apiStatus={apiStatus} />
      </div>
    </div>
  );
}

export default App;
