import React from 'react';
import { Sparkles, Coffee } from 'lucide-react';

const Footer = ({ apiStatus }) => {
  return (
    <footer className="text-center mt-12 animate-fade-in">
      {/* 상태 표시 */}
      <div className="flex items-center justify-center gap-4 mb-6">
        <div className="flex items-center gap-2">
          <div className={`w-3 h-3 rounded-full ${
            apiStatus?.status === 'healthy' ? 'bg-green-400' : 'bg-red-400'
          } animate-pulse`}></div>
          <span className="text-white/70 text-sm">
            {apiStatus?.status === 'healthy' ? '서버 연결됨' : '오프라인'}
          </span>
        </div>
        
        {apiStatus?.message_count && (
          <div className="text-white/70 text-sm">
            총 {apiStatus.message_count}개 메시지
          </div>
        )}
      </div>

      {/* 브랜딩 */}
      <div className="flex items-center justify-center gap-2 mb-4">
        <Coffee className="w-5 h-5 text-amber-300" />
        <span className="text-white/80 font-medium">
          모닝
        </span>
        <Sparkles className="w-4 h-4 text-yellow-300" />
      </div>

      {/* 설명 */}
      <p className="text-white/60 text-sm max-w-md mx-auto leading-relaxed">
        매일 새로운 영감과 동기부여를 전해드립니다.<br />
        좋은 하루 되세요! ✨
      </p>
    </footer>
  );
};

export default Footer;
