import { useState, useEffect } from 'react';

export const useMessageCounter = () => {
  const [count, setCount] = useState(0);

  // 오늘 날짜 키 생성
  const getTodayKey = () => {
    return new Date().toDateString();
  };

  // 카운터 초기화
  useEffect(() => {
    const today = getTodayKey();
    const visits = JSON.parse(localStorage.getItem('dailyVisits') || '{}');
    setCount(visits[today] || 0);
  }, []);

  // 카운터 증가
  const incrementCounter = () => {
    const today = getTodayKey();
    const visits = JSON.parse(localStorage.getItem('dailyVisits') || '{}');
    const newCount = (visits[today] || 0) + 1;
    
    visits[today] = newCount;
    localStorage.setItem('dailyVisits', JSON.stringify(visits));
    setCount(newCount);
    
    return newCount;
  };

  // 카운터 리셋 (개발용)
  const resetCounter = () => {
    const today = getTodayKey();
    const visits = JSON.parse(localStorage.getItem('dailyVisits') || '{}');
    delete visits[today];
    localStorage.setItem('dailyVisits', JSON.stringify(visits));
    setCount(0);
  };

  return {
    count,
    incrementCounter,
    resetCounter
  };
};
